Public class TastLogic { 
 	 public static void main (String[] args) { 
 		 int x = 6, y = 9; 
 		 System.out.println(x > y); 	 //แสดงผลการเปรียบเทียบว่า x มากกว่า y หรือไม่ 
 		 System.out.println(x〈 y; 	 //แสดงผลการเปรียบเทียบว่า x น้อยกว่า y หรือไม่ 
 		 System.out.println(x >= y); 
 		 System.out.println(x == y); 
 		 System.out.println(x != 6); 
 		 System.out.println((x〈 y) ＆＆ (y > 3)); 	 //แสดงผลค่าของนิพจน์สองนิพจน์ //ที่นำมา AND กัน 
 		 System.out.println((x == 5)||(y > 2)); 
 		 System.out.println(!(x > y)); 
 			 } 
 		 }

