 	 private String style; 
 	 Triangle(String s, double w, double h) { 	 //คอนสตรัคเตอร์ของคลาส Triangle 
 		 setWidth(w); 	 //กำหนดความกว้างโดยเรียกเมธอดคลาสแม่ 
 		 setHeight(h); 
 		 style = s; 	 //เรียกใช้ตัวแปร width และ height ในคลาสแม่ 
 		 }

	 class TwoDShape{ 
 	 private double width; 
 	 privat double height; 
 	 TwoDShape(double w, double h){ 	 //สร้างคอนสตรัคเตอร์กำหนดความกว้างและความสูง 
 	 width = w; 
 	 height = h; 
 		 } 
 	 double getWidth() { return width; } 
 	 double getHeight() { return height; } 
 	 void setWidth(double w) { width = w;} 
 	 void setHeight(double h) {height = h; } 
 	 void showDim({ 
 		 System.out.println("Width and height are "+width+" and "+height); 
 			 } 
 		 } 
 	 class Triangle extends TwoDShape 	 //สร้างคลาส Triangle ให้สืบทอดมาจาก 
 				 //คลาส TwoDShape 
 	 private String style; 
 	 Triangle(String s, double w, double h){ 	 //สร้างคอนสตรัคเตอร์ของคลาสนี้ 
 			 super(w, h); 	 \เรียกใช้คอนสตรัคเตอร์ในคลาสแม่ 
 			 style = s; 
 		} 
 	 double area){ 
 		 returngetWidth()*getHeight()/2; 
 		 } 
 	 voidshowStyle 
 		 System.out.println("Triangle is "+style); 
 			 } 
 		 }

