	 public class ParseIntError 
 		 { 
 		 public static void main(String[] args) 
 		 { 
 			 String str = “abcde”; 	 //กำหนดข้อมูลให้ตัวแปรสตริง 
 			 int number; 	 //ประกาศตัวแปร number สำหรับเก็บเลขจำนวนเต็ม 
 			 try 	 //ดักจับความผิดพลาดคำสั่งที่ตามมา 
 			 { 
 			 number = Intege.parseInt(str); 
 			 } 
 			 catch (NumberFormatException e) 	 //หากมีความผิดพลาดให้ทำสเตตเมนต์ที่ตามมา 
 			 { 
 			 System.out.println(“Conversion error:” + e.getMessage()); 
 			 } 
 		 } 
 		 }

	 import java.io.*; 
 	 public class ReadData { 	 public static void main(String[] args) throw IOException 	 //ใช้คำสั่ง throws IOException 
 		 { 
 		 byte[] data = new byte[10]; 
 		 System.out.println(“ป้อนตัวอักขระ”) 
 		 System.in.read(data); 	 //รับข้อมูลทางแป้นพิมพ์ 
 		 System.out.print(“คุณพิมพ์ :”); 
 		 for(int I = 0: i〈 data,length;i++) 
 		 System.out.print((char)data[i]) 	 //แสดงข้อมูลในอาร์เรย์ออกมาทีละตัว 
 			 } 
 		 }
